<!doctypehtml><html lang="en"><head><meta charset="UTF-8"><meta content="width=device-width,initial-scale=1"name="viewport"><title>Files</title><style>body{font-family:monospace;font-size:14px;background-color:#131214;color:#f7db9e;padding:10px}div{margin:0;padding:0}a{text-decoration:none;color:#f7efd9}a:visited{color:#fff}a:hover{text-decoration:underline}.a{color:#f7db9e!important}</style><script>function b(e) {var c=document.getElementById(e);var d=document.getElementById(`d-${e}`);if(c.style.display === "none"){c.style.display = "block";d.style.display = "none"; }else{c.style.display = "none";d.style.display = "inline";}}</script></head><body><?php function e($f,$g,$h){$i=scandir($f);$j=array('.','..','gabesFileLister.php','gabesFileListerMin.php','files.php','index.php','.DS_Store','.git');$i=array_diff($i,$j);usort($i,function($aa,$ab){return is_dir($ab)<=> is_dir($aa);});if(count($i)==0&&$g==0){echo "<br />No files found in this directory.";return;}foreach($i as $k){$l=$f.'/'.$k;$o="f".preg_replace('/[.\/\\ ]/','-',$l);$m="";$n=$h;if($g>0){if($k==end($i)){$m="└─ ";$n=$h.(is_dir($l)?"&nbsp;&nbsp;&nbsp;&nbsp;":"│&nbsp;&nbsp;&nbsp;");}else{$m="├─ ";$n=$h."│&nbsp;&nbsp;&nbsp;";}}if(is_dir($l)){echo($g==0)?"<br />":$h."│&nbsp;&nbsp;&nbsp; <br/>";echo $h.$m.'<a href="#l-'.$o.'" class="a" id="l-'.$o.'" onClick="b(\''.$o.'\')">'.$k.'<span style="display:none"id="d-'.$o.'"> (-)</span></a><br/><div id="'.$o.'">';e($l,$g+1,$n);echo '</div>';}else{echo $h.$m.'<a href="'.$l.'">'.$k.'</a> <br/>';}}}e('.',0,''); ?><br><br>-------<br>Displayed with <a href="https://github.com/gabrielchantayan/gabesFileLister">gabesFileLister</a> v1.4<br><br></body></html>