<!doctypehtml><html lang="en"><head><meta charset="UTF-8"><meta content="width=device-width,initial-scale=1"name="viewport"><title>Files</title><style>body{font-family:monospace;font-size:14px;background-color:#131214;color:#f7db9e;padding:10px}a{text-decoration:none;color:#f7efd9}a:visited{color:#fff}a:hover{text-decoration:underline}</style></head><body><?php function listFiles($dir,$indent,$repitition){$files=scandir($dir);$excludedFilenames=array('.','..','gabesFileLister.php','gabesFileListerMin.php','.DS_Store');$files=array_diff($files,$excludedFilenames);if(count($files)==0){echo "<br />No files found in this directory.";return;}foreach($files as $file){$filePath=$dir.'/'.$file;$newPrefix="";$newRepitition=$repitition;if($indent>0){if($file==end($files)){$newPrefix="└─ ";$newRepitition=$repitition.(is_dir($filePath)?"&nbsp;&nbsp;&nbsp;&nbsp;":"│&nbsp;&nbsp;&nbsp;");}else{$newPrefix="├─ ";$newRepitition=$repitition."│&nbsp;&nbsp;&nbsp;";}}if(is_dir($filePath)){echo($indent==0)?"<br />":$repitition."│&nbsp;&nbsp;&nbsp; <br/>";echo $repitition.$newPrefix.$file.' <br/>';listFiles($filePath,$indent+1,$newRepitition);}else{echo $repitition.$newPrefix.'<a href="'.$filePath.'">'.$file.'</a> <br/>';}}}listFiles('.',0,''); ?><br><br>-------<br>Displayed with <a href="https://github.com/gabrielchantayan/gabesFileLister">gabesFileLister</a><br><br></body></html>